<template>    <el-dialog :visible="dialogFormVisible" @close="close" width="443px" height="268px">        <div class="content">            <div class="address">                <span class="title">所在地区</span>                <div class="actions">                    <el-select size="mini" v-model="resource.address.province.name">                        <el-option v-for="(item,index) in province" :key="index" :value="item.value"></el-option>                    </el-select>                    <el-select size="mini" v-model="resource.address.city.name">                        <el-option v-for="(item,index) in city" :key="index" :value="item.value"></el-option>                    </el-select>                    <el-select size="mini" v-model="resource.address.town.name">                        <el-option v-for="(item,index) in town" :key="index" :value="item.value"></el-option>                    </el-select>                </div>            </div>            <div class="owner">                <span class="title">收货人</span>                <div class="actions">                    <input type="text" placeholder="收货人" v-model="resource.owner">                </div>            </div>            <div class="addressDetails">                <span class="title">详细地址</span>                <div class="actions">                    <input type="text" placeholder="详细地址" v-model="resource.addressDetails">                </div>            </div>            <div class="phone">                <span class="title">手机</span>                <div class="actions">                    <input type="text" placeholder="手机" v-model="resource.phone">                </div>            </div>        </div>        <div class="submit-wrap">            <span class="submit" @click="submit">保存信息</span>        </div>    </el-dialog></template><script>  export default {    name: "dialog",    props: {      dialogFormVisible: {        type:Boolean,        default: false      }    },    data(){      return {        province: [],        city: [],        town: [],        resource: {          address: {province: {name: ""},city: {name: ""}, town: {name: ""}},          owner: "",          addressDetails: "",          phone: ""        }      }    },    methods: {      submit(){        this.$emit('submit',this.resource)      },      close(){        this.$emit('close')      }    }  }</script><style scoped lang="scss">    @import "../../style/global";    .content {        display: flex; justify-content: center;        flex-direction: column;        > div {            display: flex; align-items: center; margin: 9px 0;            .actions { flex: 1; display: flex;                input { flex: 1; border: 1px solid #ddd; padding: 4px; color: #333;                    &::placeholder { color: #ddd; }                }            }            .title { width: 64px;white-space:nowrap;  }        }    }    .submit-wrap { display: flex; justify-content: center;        .submit { cursor: pointer; background: $orange-active; color: #fff; height: 26px; width: 114px;            border-radius: 2px; display: flex; justify-content: center; align-items: center;        }    }</style>