<template>    <div class="give-type">        <h3>配送方式</h3>        <div class="item-content">            <div class="type-button">                <span>快速运输</span>            </div>            <div class="tips">                <h4>退改无忧</h4>                <div>该商品支持7天退货，15天内换货</div>                <div class="details">查看详情</div>            </div>            <div class="invoice">                <h4>发票信息</h4>                <div class="actions">                    <span :class="{active : invoiceType === 'no'}" @click="invoiceType = 'no'">不开发票</span>                    <span :class="{active : invoiceType === 'default'}" @click="invoiceType = 'default'">普通发票</span>                    <span :class="{active : invoiceType === 'add'}" @click="invoiceType = 'add'">增值税发票</span>                </div>            </div>        </div>    </div></template><script>  export default {    name: "GiveType",    data(){      return {        invoiceType: "no"      }    }  }</script><style scoped lang="scss">    @import "../../style/global";    @mixin g-active-button-style(){        border: 2px solid $orange-active; border-radius: 2px; width: 100px; height: 30px;        display: flex; justify-content: center; align-items: center; position: relative;        &:after { content: "";  position: absolute; right: 0; bottom: 0;            background-image: url("../../assets/button-active.png");            width: 20px; height: 20px;        }    }    @mixin g-button-default-style(){        display: flex; justify-content: center; align-items: center;        width:100px; height:30px;        background:rgba(255,255,255,1); border:1px solid rgba(221,221,221,1);        border-radius:2px; margin: 0 7.5px;    }    .give-type {        h3 { color: $order-title-color; font-size: 16px; font-weight: bold; padding: 16px 0 24px 0;}        .tips,.type-button { border-bottom: 1px solid #ddd; padding-bottom: 20px;}        .tips,.invoice { padding-top: 16px; }        .item-content { padding-left: 22px;  padding-right: 51px; border-right: 1px solid #ddd;            h4 { margin-bottom: 24px;font-weight: 500; }            > .type-button > span { @include g-active-button-style(); }            .tips {  .details { font-size: 11px; color: #1A60ED; padding-top: 10px; }  }            .actions { display: flex;                > span { @include g-button-default-style(); cursor: pointer;                    &.active { @include g-active-button-style(); }                }            }        }    }</style>