<template>    <div class="payment-type">        <h3>支付方式</h3>        <div class="item-content">            <span @click="type = 'bank'" :class="{active : type==='bank'}">                <img src="../../assets/bank.png" alt="">                银联            </span>            <span @click="type = 'wechat'" :class="{active : type==='wechat'}">                <img src="../../assets/wechat.png" alt="">                微信支付            </span>            <span @click="type = 'alipay'" :class="{active : type==='alipay'}">                <img src="../../assets/alipay.png" alt="">                支付宝            </span>        </div>    </div></template><script>  export default {    name: "PaymentType",    data(){      return {        type: "bank"      }    },  }</script><style scoped lang="scss">    @import "../../style/global";    @mixin g-active-button-style(){        border: 2px solid $orange-active; border-radius: 2px; width: 100px; height: 30px;        display: flex; justify-content: center; align-items: center; position: relative;        &:after { content: "";  position: absolute; right: 0; bottom: 0;            background-image: url("../../assets/button-active.png");            width: 20px; height: 20px;        }    }    @mixin g-button-default-style(){        display: flex; justify-content: center; align-items: center;        width:100px; height:30px;        background:rgba(255,255,255,1); border:1px solid rgba(221,221,221,1);        border-radius:2px; margin: 0 7.5px;    }    .payment-type {        border-bottom: 1px solid #ddd; height: 137px;        h3 { color: $order-title-color; font-size: 16px; font-weight: bold; padding: 16px 0 24px 0; }        .item-content { padding-left: 22px; display: flex; align-items: center;            > span { cursor: pointer;                @include g-button-default-style();                &.active { @include g-active-button-style();}            }        }    }</style>